package com.gatetech.content;import java.io.Serializable;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;public class ContactContent implements Serializable {    /**     * An array of sample (dummy) items.     */    public List<ContactItem> ITEMS = new ArrayList<ContactItem>();    /**     * A map of sample (dummy) items, by ID.     */    public  Map<String, ContactItem> ITEM_MAP = new HashMap<String, ContactItem>();    public ContactContent() {    }    public void addItem(ContactContent.ContactItem item) {        ITEMS.add(item);        ITEM_MAP.put(item.contact.toString(), item);    }    public void updateItem(String type,String value) {        for(ContactContent.ContactItem rw: ITEMS) {            if(type.toUpperCase().equals(rw.type.toUpperCase())) {                rw.value = value;            }        }    }    public ContactContent.ContactItem getItem(String type) {        for(ContactContent.ContactItem rw: ITEMS) {            if(type.toUpperCase().equals(rw.type.toUpperCase())) {                return rw;            }        }        return new ContactContent.ContactItem(0,"","");    }    public static class ContactItem implements Serializable {        public Integer contact;        public String type;        public String value;        public ContactItem(Integer contact, String type,String value) {            this.contact = contact;            this.type = type;            this.value= value;        }    }}